    <script>
        // Función para cambiar el idioma del texto de privacidad
        function cambiarIdioma(idioma) {
            var textoCookies = document.getElementById("texto-privacidad").innerHTML.trim();
            // Realizar una solicitud AJAX para traducir el texto
            var xhr = new XMLHttpRequest();
            xhr.onreadystatechange = function() {
                if (xhr.readyState === XMLHttpRequest.DONE) {
                    if (xhr.status === 200) {
                        // Actualizar el texto de privacidad con la traducción
                        document.getElementById("texto-privacidad").innerHTML = xhr.responseText;
                    } else {
                        console.error('Error al realizar la solicitud.');
                    }
                }
            };
            xhr.open('POST', 'traducir_texto.php', true);
            xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
            xhr.send('texto=' + encodeURIComponent(textoCookies) + '&idioma=' + idioma);
        }
    </script>
